mapper.select:
SELECT * FROM LEAF_TAG
IS_DELETED = FALSE
AND MEMBER_ID = #{memberId}
AND NAME LIKE CONCAT('%', #{name}, '%')
ORDER BY ID DESC LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE LEAF_TAG
SET NAME = #{name}
WHERE ID = #{id}
    AND IS_DELETED = FALSE

mapper.delete:
UPDATE LEAF_TAG
SET IS_DELETED = TRUE
WHERE ID = #{id}

UPDATE LEAF_TAG
SET IS_DELETED = TRUE
WHERE ID = #{id}
